public class AdvAwardsSummaryOtherVOImpl extends OAViewObjectImpl {
    /**This is the default constructor (do not remove)
     */
    public AdvAwardsSummaryOtherVOImpl() {
    }

    //  WebFlag: 'QUERY',查询页面, 'SUMMARY',汇总页面

    public void initSQL(int ideptID, String distDate, String WebFlag) {
        String strSQL = getSummarySQL(ideptID, distDate, WebFlag, "EXTERNAL");

        cancelQuery();
        setQuery(strSQL);
        setMaxFetchSize(-1);
        executeQuery();
    }

    /* 获取查询SQL */

    private String getSummarySQL(int iDeptID, String strDistDate, 
                                 String strWebFlag, String strDeptFlag) {
        AdvanceAwardsAMImpl amImpl = 
            (AdvanceAwardsAMImpl)this.getApplicationModule();
        CallableStatement cs = null;
        OADBTransaction transaction = amImpl.getOADBTransaction();
        Clob cSQL = null;
        String strSQL = "";
        cs = 
 transaction.createCallableStatement("call CUX_HR_BONUS.GET_DEPT_SUM_SQL(?, ?, ?, ?, ?)", 
                                     1);
        try {
            cs.setInt(1, iDeptID);
            cs.setString(2, strDistDate);
            cs.setString(3, strWebFlag);
            cs.setString(4, strDeptFlag);
            cs.registerOutParameter(5, Types.CLOB);
            cs.executeUpdate();
            cSQL = cs.getClob(5);
            Reader inStream = cSQL.getCharacterStream();
            char[] c = new char[(int)cSQL.length()];
            inStream.read(c);
            strSQL = new String(c);
            inStream.close();
            cs.close();
        } catch (Exception e) {
            System.out.println(e.toString());
        }
        //System.out.println("otherSum:"+ strSQL);
        return strSQL;
    } //end getSummarySQL

}
